mapper.update:
UPDATE REST SET
rest_check = 'T', rest_result =#{restResult}, rest_confirm = to_char(sysdate, 'yyyy-MM-dd') 
WHERE rest_no =#{restNo}

mapper.select:
SELECT member_id 
FROM REST
WHERE rest_no =#{restNo}

SELECT notice_no
FROM REST
WHERE rest_no =#{restNo}

mapper.insert:
INSERT into rest 
VALUES(SEQ_REST.nextval
		, #{noticeNo}
		, #{memberId}
		, #{reason}
		, to_char(sysdate, 'yyyy-MM-dd')
		, null 
		,'F'
		, null)
